import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col, ListGroup, Form, Button } from 'react-bootstrap';

const API_BASE = 'http://localhost:8081/api/messages';

function App() {
  const [messages, setMessages] = useState([]);
  const [conversations, setConversations] = useState({});
  const [selectedUser, setSelectedUser] = useState(null);
  const [input, setInput] = useState('');

  // Format timestamp nicely
  const formatTimestamp = (ts) => {
    const d = new Date(ts);
    return d.toLocaleString([], {
      hour: '2-digit',
      minute: '2-digit',
      day: 'numeric',
      month: 'short',
    });
  };

  // Group messages by "from" user
  const groupMessages = (msgs) => {
    const convos = {};
    msgs.forEach((msg) => {
      const user = msg.from || 'unknown';
      if (!convos[user]) convos[user] = [];
      convos[user].push(msg);
    });
    return convos;
  };

  // Fetch messages from backend
  const fetchMessages = async () => {
    try {
      const res = await axios.get(API_BASE);
      setMessages(res.data);
      setConversations(groupMessages(res.data));
    } catch (err) {
      console.error('Failed to fetch messages:', err);
      alert('Failed to load messages from backend.');
    }
  };

  useEffect(() => {
    fetchMessages();
    // Optionally refresh every 10s:
    const interval = setInterval(fetchMessages, 10000);
    return () => clearInterval(interval);
  }, []);

  // Send a new message
  const sendMessage = async () => {
    if (!input.trim() || !selectedUser) return;
    const newMsg = {
      id: 'msg' + Date.now(),
      from: 'me',
      to: selectedUser,
      text: input.trim(),
      timestamp: new Date().toISOString(),
      status: 'sent',
    };

    // Optimistic UI update
    setMessages((prev) => [...prev, newMsg]);
    setConversations((prev) => ({
      ...prev,
      [selectedUser]: prev[selectedUser] ? [...prev[selectedUser], newMsg] : [newMsg],
    }));
    setInput('');

    try {
      await axios.post(API_BASE, newMsg);
    } catch (err) {
      alert('Failed to send message to backend.');
      console.error(err);
    }
  };

  return (
    <Container fluid className="vh-100 d-flex p-0" style={{ fontFamily: 'Arial, sans-serif' }}>
      {/* Sidebar */}
      <Col xs={4} md={3} className="border-end d-flex flex-column p-0">
        <div className="p-3 border-bottom">
          <h4>Chats</h4>
        </div>
        <ListGroup className="flex-grow-1 overflow-auto">
          {Object.entries(conversations).map(([user, conv]) => {
            const lastMsg = conv[conv.length - 1];
            return (
              <ListGroup.Item
                action
                key={user}
                active={user === selectedUser}
                onClick={() => setSelectedUser(user)}
                style={{ cursor: 'pointer' }}
              >
                <div className="d-flex justify-content-between">
                  <strong>{user}</strong>
                  <small className="text-muted">{formatTimestamp(lastMsg.timestamp)}</small>
                </div>
                <div className="text-truncate">{lastMsg.text || '(no message)'}</div>
              </ListGroup.Item>
            );
          })}
        </ListGroup>
      </Col>

      {/* Main Chat */}
      <Col xs={8} md={9} className="d-flex flex-column p-0">
        <div className="p-3 border-bottom fw-bold">
          {selectedUser ? `Chat with ${selectedUser}` : 'Select a chat'}
        </div>
        <div
          className="flex-grow-1 overflow-auto p-3"
          style={{ backgroundColor: '#f7f7f7', display: 'flex', flexDirection: 'column' }}
        >
          {selectedUser &&
            conversations[selectedUser]?.map((msg) => (
              <div
                key={msg.id}
                className={`p-2 mb-3 rounded-3 position-relative ${
                  msg.from === selectedUser ? 'bg-white align-self-start' : 'bg-success text-white align-self-end'
                }`}
                style={{ maxWidth: '70%', fontSize: '0.9rem', wordWrap: 'break-word' }}
              >
                <div>{msg.text}</div>
                <div
                  className="position-absolute"
                  style={{ bottom: 4, right: 8, fontSize: '0.75rem', opacity: 0.7 }}
                >
                  {formatTimestamp(msg.timestamp)} {msg.status && `â€¢ ${msg.status}`}
                </div>
              </div>
            ))}
        </div>
        {selectedUser && (
          <Form
            className="p-3 border-top d-flex"
            onSubmit={(e) => {
              e.preventDefault();
              sendMessage();
            }}
          >
            <Form.Control
              type="text"
              placeholder="Type a message..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              autoComplete="off"
            />
            <Button variant="success" type="submit" disabled={!input.trim()} className="ms-2">
              Send
            </Button>
          </Form>
        )}
      </Col>
    </Container>
  );
}

export default App;
