import React, { useEffect, useState } from "react";
import axios from "axios";

const BACKEND_URL = "http://localhost:8081/api/messages";

export default function App() {
  const [messages, setMessages] = useState([]);
  const [conversations, setConversations] = useState({});
  const [selectedUser, setSelectedUser] = useState(null);
  const [inputText, setInputText] = useState("");

  // Fetch messages on mount
  useEffect(() => {
    fetchMessages();
  }, []);

  // Group messages by sender (you can adjust key if needed)
  useEffect(() => {
    const grouped = messages.reduce((acc, msg) => {
      const userId = msg.from || "unknown";
      if (!acc[userId]) acc[userId] = [];
      acc[userId].push(msg);
      return acc;
    }, {});
    setConversations(grouped);
  }, [messages]);

  async function fetchMessages() {
    try {
      const res = await axios.get(BACKEND_URL);
      setMessages(res.data);
    } catch (err) {
      console.error("Failed to fetch messages", err);
    }
  }

  async function sendMessage() {
    if (!inputText.trim() || !selectedUser) return;

    const newMsg = {
      id: `local-${Date.now()}`,
      from: "me",        // or your user id
      to: selectedUser,
      text: inputText,
      timestamp: new Date(),
      status: "sent",
    };

    try {
      // Optimistic UI update
      setMessages((msgs) => [...msgs, newMsg]);

      // Send to backend
      await axios.post(BACKEND_URL, {
        id: newMsg.id,
        from: newMsg.from,
        to: newMsg.to,
        text: newMsg.text,
        timestamp: newMsg.timestamp,
        status: newMsg.status,
      });

      setInputText("");
    } catch (err) {
      console.error("Failed to send message", err);
    }
  }

  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Arial, sans-serif" }}>
      {/* Sidebar: conversations */}
      <div style={{ width: 300, borderRight: "1px solid #ccc", overflowY: "auto" }}>
        <h3 style={{ padding: 10 }}>Chats</h3>
        {Object.keys(conversations).map((userId) => {
          const lastMsg = conversations[userId][conversations[userId].length - 1];
          return (
            <div
              key={userId}
              style={{
                padding: 10,
                borderBottom: "1px solid #eee",
                cursor: "pointer",
                backgroundColor: selectedUser === userId ? "#dcdcdc" : "transparent",
              }}
              onClick={() => setSelectedUser(userId)}
            >
              <strong>{userId}</strong>
              <br />
              <small style={{ color: "#555" }}>
                {lastMsg.text.length > 30
                  ? lastMsg.text.substring(0, 30) + "..."
                  : lastMsg.text}
              </small>
              <br />
              <small style={{ color: "#999", fontSize: 10 }}>
                {new Date(lastMsg.timestamp).toLocaleString()}
              </small>
            </div>
          );
        })}
      </div>

      {/* Chat Window */}
      <div style={{ flex: 1, display: "flex", flexDirection: "column" }}>
        <div
          style={{
            padding: 10,
            borderBottom: "1px solid #ccc",
            backgroundColor: "#eee",
            fontWeight: "bold",
          }}
        >
          {selectedUser ? `Chat with ${selectedUser}` : "Select a chat"}
        </div>

        <div
          style={{
            flex: 1,
            padding: 10,
            overflowY: "auto",
            backgroundColor: "#f9f9f9",
          }}
        >
          {selectedUser && conversations[selectedUser] ? (
            conversations[selectedUser].map((msg) => (
              <div
                key={msg.id}
                style={{
                  marginBottom: 10,
                  textAlign: msg.from === "me" ? "right" : "left",
                }}
              >
                <div
                  style={{
                    display: "inline-block",
                    padding: "8px 12px",
                    borderRadius: 20,
                    backgroundColor: msg.from === "me" ? "#DCF8C6" : "#fff",
                    border: "1px solid #ccc",
                    maxWidth: "70%",
                  }}
                >
                  {msg.text}
                  <br />
                  <small style={{ fontSize: 10, color: "#888" }}>
                    {new Date(msg.timestamp).toLocaleTimeString()}
                    {"  "}
                    {msg.status === "sent" ? "✓" : msg.status === "delivered" ? "✓✓" : msg.status === "read" ? "✓✓ (read)" : ""}
                  </small>
                </div>
              </div>
            ))
          ) : (
            <p style={{ color: "#999" }}>No chat selected</p>
          )}
        </div>

        {/* Input box */}
        {selectedUser && (
          <div style={{ padding: 10, borderTop: "1px solid #ccc", display: "flex" }}>
            <input
              type="text"
              placeholder="Type a message..."
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              style={{ flex: 1, padding: 10, borderRadius: 20, border: "1px solid #ccc" }}
              onKeyDown={(e) => e.key === "Enter" && sendMessage()}
            />
            <button
              onClick={sendMessage}
              style={{
                marginLeft: 10,
                padding: "10px 20px",
                borderRadius: 20,
                backgroundColor: "#4caf50",
                color: "white",
                border: "none",
                cursor: "pointer",
              }}
            >
              Send
            </button>
          </div>
        )}
      </div>
    </div>
  );
}
